import "./Listing.css";
import { CSSTransition } from "react-transition-group";
import { useState } from "react";

function Listing(props) {
    const [showFullListing, setShowFullListing] = useState(false);

    return (
        <div>
            <ul class="cards">
                <li>
                    <div
                        class="card"
                        onClick={() => setShowFullListing(!showFullListing)}
                    >
                        <img src={props.image} class="card__image" alt="" />
                        <CSSTransition
                            in={showFullListing}
                            timeout={5000}
                            classNames="card__overlay"
                        >
                            <div class="">
                                <div class="card__header">
                                    <svg
                                        class="card__arc"
                                        xmlns="http://www.w3.org/2000/svg"
                                    >
                                        <path />
                                    </svg>
                                    <div class="card__header-text">
                                        <h3 class="card__title">
                                            {props.name}
                                        </h3>

                                        <span class="card__status">
                                            1 hour ago
                                        </span>
                                    </div>
                                </div>
                                <p class="card__description">{props.desc}</p>
                            </div>
                        </CSSTransition>
                    </div>
                </li>
            </ul>
        </div>
    );
}

export default Listing;
