import "./Listing.css";
import { useEffect, useState } from "react";

function Listing(props) {
    const [date, setDate] = useState();
    const [card__overlay, setcard__overlay] = useState("card__overlay_min");
    const [showFullListing, setShowFullListing] = useState(false);
    const [scrollTrigger, setScrollTrigger] = useState(false);

    function formatAssociations(arr) {}
    function formatDate(string) {
        var options = { year: "numeric", month: "long", day: "numeric" };
        return new Date(string).toLocaleDateString([], options);
    }
    useEffect(() => {
        setDate(formatDate(props.date_created));
    }, [props.date_created]);

    useEffect(() => {
        if (showFullListing && scrollTrigger) {
            setcard__overlay("card__overlay_full_scrollable");
        } else if (showFullListing) {
            setcard__overlay("card__overlay_full");
        } else {
            setcard__overlay("card__overlay_min");
        }
    }, [showFullListing, scrollTrigger]);

    return (
        <div>
            <li>
                <div
                    class="card"
                    onClick={() => {
                        setShowFullListing(!showFullListing);
                        if (!showFullListing) {
                            setTimeout(() => {
                                console.log("yomoms");
                                setScrollTrigger(true);
                            }, 250);
                        } else {
                            setScrollTrigger(false);
                        }
                    }}
                >
                    <img src={props.image} class="card__image" alt="" />
                    <div className={card__overlay}>
                        <div
                            class={
                                showFullListing
                                    ? "card__header_stay"
                                    : "card__header_move"
                            }
                        >
                            <svg
                                class="card__arc"
                                xmlns="http://www.w3.org/2000/svg"
                            >
                                <path />
                            </svg>
                            <h3 class="card__title">{props.name}</h3>
                            <div class="card__header-text">
                                <span class="card__address">
                                    {showFullListing
                                        ? props.address +
                                          ", " +
                                          props.city +
                                          ", " +
                                          props.state +
                                          " " +
                                          props.zip
                                        : props.address}
                                </span>
                                <span
                                    class={
                                        props.status
                                            ? "card__active"
                                            : "card__inactive"
                                    }
                                >
                                    {props.status ? "ACTIVE" : "INACTIVE"}
                                </span>
                            </div>
                        </div>
                        <p class="card__description">{props.desc}</p>
                        <p class="card__specs">
                            ID: {props.id} | Type: {props.type} | Date Created:{" "}
                            {date} | Associated with {props.assoc}
                        </p>
                    </div>
                </div>
            </li>
        </div>
    );
}

export default Listing;
