import "./Listing.css";
import { useEffect, useState } from "react";

function Listing(props) {
    const [hours, setHours] = useState("");
    const [assoc, setAssoc] = useState([]);
    const [date, setDate] = useState("");
    const [card__overlay, setcard__overlay] = useState("card__overlay_min");
    const [showFullListing, setShowFullListing] = useState(false);
    const [scrollTrigger, setScrollTrigger] = useState(false);

    function formatHours(obj) {
        if (obj === null) {
            return "";
        }

        console.log(props.hours.Sunday);
        return (
            <div className="card__description">
                <b>Hours:</b>
                <table className="card__hourstable">
                    <tbody>
                        <tr>
                            <td>Sunday:</td>
                            <td>2</td>
                        </tr>
                        <tr>
                            <td>Monday:</td>
                            <td>4</td>
                        </tr>
                        <tr>
                            <td>Tuesday:</td>
                            <td>6</td>
                        </tr>
                        <tr>
                            <td>Wednesday:</td>
                            <td>8</td>
                        </tr>
                        <tr>
                            <td>Thursday:</td>
                            <td>10</td>
                        </tr>
                        <tr>
                            <td>Friday:</td>
                            <td>12</td>
                        </tr>
                        <tr>
                            <td>Saturday:</td>
                            <td>14</td>
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }
    function formatAssociations(...arr) {
        if (arr[0] === null || arr[0].length === 0) {
            return "";
        }

        let foo = arr[0];
        if (foo.length < 2) {
            return (
                "| Associated with " +
                props.data.find((business) => business.business_uid === foo[0])
                    .business_name
            );
        } else if (foo.length == 2) {
            return (
                "| Associated with " +
                props.data.find((business) => business.business_uid === foo[0])
                    .business_name +
                " & " +
                props.data.find((business) => business.business_uid === foo[1])
                    .business_name
            );
        } else {
            foo[foo.length - 1] =
                " & " +
                props.data.find(
                    (business) => business.business_uid === foo[foo.length - 1]
                ).business_name;
            for (let i = 0; i < foo.length - 1; i++) {
                foo[i] =
                    " " +
                    props.data.find(
                        (business) => business.business_uid === foo[i]
                    ).business_name;
            }
            return "| Associated with " + foo;
        }
    }
    function formatDate(string) {
        var options = { year: "numeric", month: "long", day: "numeric" };
        return new Date(string).toLocaleDateString([], options);
    }

    useEffect(() => {
        setHours(formatHours(props.hours));
    }, [props.hours]);
    useEffect(() => {
        setAssoc(formatAssociations(props.assoc));
    }, [props.assoc]);
    useEffect(() => {
        setDate(formatDate(props.date_created));
    }, [props.date_created]);

    useEffect(() => {
        if (showFullListing && scrollTrigger) {
            setcard__overlay("card__overlay_full_scrollable");
        } else if (showFullListing) {
            setcard__overlay("card__overlay_full");
        } else {
            setcard__overlay("card__overlay_min");
        }
    }, [showFullListing, scrollTrigger]);

    return (
        <div>
            <li>
                <div
                    className="card"
                    onClick={() => {
                        setShowFullListing(!showFullListing);
                        if (!showFullListing) {
                            setTimeout(() => {
                                setScrollTrigger(true);
                            }, 250);
                        } else {
                            setScrollTrigger(false);
                        }
                    }}
                >
                    <img src={props.image} className="card__image" alt="" />
                    <div className={card__overlay}>
                        <div
                            className={
                                showFullListing
                                    ? "card__header_stay"
                                    : "card__header_move"
                            }
                        >
                            <div className="card__header_arc"></div>
                            <h3 className="card__title">
                                {props.name}{" "}
                                {props.type !== "" ? " Â· " + props.type : ""}
                            </h3>
                            <div className="card__header-text">
                                <span className="card__address">
                                    {showFullListing
                                        ? props.address +
                                          " " +
                                          props.unit +
                                          ", " +
                                          props.city +
                                          ", " +
                                          props.state +
                                          " " +
                                          props.zip
                                        : props.address + " " + props.unit}
                                </span>
                                <span
                                    className={
                                        props.status === "ACTIVE"
                                            ? "card__active"
                                            : "card__inactive"
                                    }
                                >
                                    {props.status === "ACTIVE"
                                        ? "ACTIVE"
                                        : "INACTIVE"}
                                </span>
                            </div>
                        </div>
                        <p className="card__description">{props.desc}</p>
                        {hours}
                        <div className="card__description">
                            <b>Contact:</b>
                        </div>
                        <p className="card__specs">
                            ID: {props.id} | Date Created: {date} {assoc}
                        </p>
                    </div>
                </div>
            </li>
        </div>
    );
}

export default Listing;
