import "./Listing.css";
import { useEffect, useState } from "react";

function Listing(props) {
    const [assoc, setAssoc] = useState();
    const [date, setDate] = useState();
    const [card__overlay, setcard__overlay] = useState("card__overlay_min");
    const [showFullListing, setShowFullListing] = useState(false);
    const [scrollTrigger, setScrollTrigger] = useState(false);

    function formatAssociations(...arr) {
        console.log(arr[0]);
        if (arr[0] === null || arr[0].length === 0) {
            return "";
        }

        let foo = arr[0];
        if (foo.length < 2) {
            console.log("in");
            return "| Associated with " + foo[0];
        } else if (foo.length == 2) {
            return "| Associated with " + foo[0] + " & " + foo[1];
        } else {
            foo[foo.length - 1] = "& " + foo[foo.length - 1];
            for (let i = 0; i < foo.length - 1; i++) {
                foo[i] = foo[i] + ", ";
            }
            return "| Associated with " + foo;
        }
    }
    useEffect(() => {
        setAssoc(formatAssociations(props.assoc));
    }, [props.assoc]);
    function formatDate(string) {
        var options = { year: "numeric", month: "long", day: "numeric" };
        return new Date(string).toLocaleDateString([], options);
    }
    useEffect(() => {
        setDate(formatDate(props.date_created));
    }, [props.date_created]);

    useEffect(() => {
        if (showFullListing && scrollTrigger) {
            setcard__overlay("card__overlay_full_scrollable");
        } else if (showFullListing) {
            setcard__overlay("card__overlay_full");
        } else {
            setcard__overlay("card__overlay_min");
        }
    }, [showFullListing, scrollTrigger]);

    return (
        <div>
            <li>
                <div
                    className="card"
                    onClick={() => {
                        setShowFullListing(!showFullListing);
                        if (!showFullListing) {
                            setTimeout(() => {
                                setScrollTrigger(true);
                            }, 250);
                        } else {
                            setScrollTrigger(false);
                        }
                    }}
                >
                    <img src={props.image} className="card__image" alt="" />
                    <div className={card__overlay}>
                        <div
                            className={
                                showFullListing
                                    ? "card__header_stay"
                                    : "card__header_move"
                            }
                        >
                            <div className="card__header_arc"></div>
                            <h3 className="card__title">
                                {props.name}{" "}
                                {props.type !== "" ? " · " + props.type : ""}
                            </h3>
                            <div className="card__header-text">
                                <span className="card__address">
                                    {showFullListing
                                        ? props.address +
                                          " " +
                                          props.unit +
                                          ", " +
                                          props.city +
                                          ", " +
                                          props.state +
                                          " " +
                                          props.zip
                                        : props.address + " " + props.unit}
                                </span>
                                <span
                                    className={
                                        props.status === "ACTIVE"
                                            ? "card__active"
                                            : "card__inactive"
                                    }
                                >
                                    {props.status === "ACTIVE"
                                        ? "ACTIVE"
                                        : "INACTIVE"}
                                </span>
                            </div>
                        </div>
                        <p className="card__description">{props.desc}</p>
                        <p className="card__specs">
                            ID: {props.id} | Date Created: {date} {assoc}
                        </p>
                    </div>
                </div>
            </li>
        </div>
    );
}

export default Listing;
