import "./Listing.css";
import { useState } from "react";

function Listing(props) {
    const [showFullListing, setShowFullListing] = useState(false);
    const [scrollTrigger, setScrollTrigger] = useState(false);

    return (
        <div>
            <li>
                <div
                    class="card"
                    onClick={() => {
                        setShowFullListing(!showFullListing);
                        if (showFullListing) {
                            setTimeout(() => {
                                setScrollTrigger(true);
                            }, 3000);
                        } else {
                            setScrollTrigger(true);
                        }
                    }}
                >
                    <img src={props.image} class="card__image" alt="" />
                    <div
                        className={() => {
                            if (showFullListing && scrollTrigger) {
                                return "card__overlay_full_scrollable";
                            } else if (showFullListing) {
                                return "card__overlay_full";
                            } else {
                                return "card__overlay_min";
                            }
                        }}
                    >
                        <div
                            class={
                                showFullListing
                                    ? "card__header_stay"
                                    : "card__header_move"
                            }
                        >
                            <svg
                                class="card__arc"
                                xmlns="http://www.w3.org/2000/svg"
                            >
                                <path />
                            </svg>
                            <h3 class="card__title">{props.name}</h3>
                            <div class="card__header-text">
                                <span class="card__address">
                                    {showFullListing
                                        ? props.address +
                                          ", " +
                                          props.city +
                                          ", " +
                                          props.state +
                                          " " +
                                          props.zip
                                        : props.address}
                                </span>
                                <span
                                    class={
                                        props.status
                                            ? "card__active"
                                            : "card__inactive"
                                    }
                                >
                                    {props.status ? "ACTIVE" : "INACTIVE"}
                                </span>
                            </div>
                        </div>
                        <p class="card__description">{props.desc}</p>
                    </div>
                </div>
            </li>
        </div>
    );
}

export default Listing;
